package com.hdil.saluschart.ui.compose.components

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.lazy.LazyColumn
import androidx.compose.foundation.lazy.items
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material3.MaterialTheme
import androidx.compose.material3.Text
import androidx.compose.runtime.Composable
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import com.hdil.saluschart.core.chart.ChartPoint

/**
 * 차트 범례를 Composable로 표시합니다.
 *
 * @param modifier 모디파이어
 * @param labels 범례 항목 레이블 목록 (직접 제공된 경우)
 * @param chartData 차트 데이터 포인트 목록 (레이블을 추출할 경우)
 * @param colors 각 항목에 사용한 색상 목록
 * @param title 범례 제목 (기본값: null)
 * @param colorBoxSize 색상 상자 크기
 * @param textSize 텍스트 크기
 * @param spacing 항목 간 간격
 */
@Composable
fun ChartLegend(
    modifier: Modifier = Modifier,
    labels: List<String>? = null,
    chartData: List<ChartPoint>? = null,
    colors: List<Color>,
    title: String? = null,
    colorBoxSize: androidx.compose.ui.unit.Dp = 12.dp,
    textSize: androidx.compose.ui.unit.TextUnit = 12.sp,
    spacing: androidx.compose.ui.unit.Dp = 8.dp
) {
    val legendLabels = labels ?: chartData?.mapIndexed { i, point ->
        point.label ?: "항목 ${i + 1}"
    } ?: emptyList()

    Column(
        modifier = modifier,
        verticalArrangement = Arrangement.spacedBy(spacing)
    ) {
        // 범례 제목 (제공된 경우)
        title?.let {
            Text(
                text = it,
                style = MaterialTheme.typography.titleSmall,
                fontWeight = FontWeight.Bold,
                fontSize = (textSize.value + 2).sp,
                color = MaterialTheme.colorScheme.onSurface
            )
            Spacer(modifier = Modifier.height(4.dp))
        }

        // 각 범례 항목
        legendLabels.forEachIndexed { index, label ->
            if (index < colors.size) {
                LegendItem(
                    color = colors[index],
                    label = label,
                    colorBoxSize = colorBoxSize,
                    textSize = textSize,
                    spacing = spacing
                )
            }
        }
    }
}

/**
 * 범례의 개별 항목을 Composable로 표시합니다.
 *
 * @param color 색상
 * @param label 레이블 텍스트
 * @param colorBoxSize 색상 상자 크기
 * @param textSize 텍스트 크기
 * @param spacing 상자와 텍스트 사이 간격
 */
@Composable
fun LegendItem(
    color: Color,
    label: String,
    colorBoxSize: androidx.compose.ui.unit.Dp = 12.dp,
    textSize: androidx.compose.ui.unit.TextUnit = 12.sp,
    spacing: androidx.compose.ui.unit.Dp = 8.dp
) {
    Row(
        verticalAlignment = Alignment.CenterVertically,
        horizontalArrangement = Arrangement.spacedBy(spacing)
    ) {
        // 색상 상자
        Box(
            modifier = Modifier
                .size(colorBoxSize)
                .background(
                    color = color,
                    shape = RoundedCornerShape(2.dp)
                )
        )

        // 레이블 텍스트
        Text(
            text = label,
            fontSize = textSize,
            color = MaterialTheme.colorScheme.onSurface,
            style = MaterialTheme.typography.bodySmall
        )
    }
}

/**
 * 스크롤 가능한 차트 범례를 Composable로 표시합니다.
 *
 * @param modifier 모디파이어
 * @param labels 범례 항목 레이블 목록 (직접 제공된 경우)
 * @param chartData 차트 데이터 포인트 목록 (레이블을 추출할 경우)
 * @param colors 각 항목에 사용한 색상 목록
 * @param title 범례 제목 (기본값: null)
 * @param colorBoxSize 색상 상자 크기
 * @param textSize 텍스트 크기
 * @param spacing 항목 간 간격
 * @param maxHeight 최대 높이 (이를 초과하면 스크롤)
 */
@Composable
fun ScrollableChartLegend(
    modifier: Modifier = Modifier,
    labels: List<String>? = null,
    chartData: List<ChartPoint>? = null,
    colors: List<Color>,
    title: String? = null,
    colorBoxSize: androidx.compose.ui.unit.Dp = 12.dp,
    textSize: androidx.compose.ui.unit.TextUnit = 12.sp,
    spacing: androidx.compose.ui.unit.Dp = 8.dp,
    maxHeight: androidx.compose.ui.unit.Dp = 200.dp
) {
    val legendLabels = labels ?: chartData?.mapIndexed { i, point ->
        point.label ?: "항목 ${i + 1}"
    } ?: emptyList()

    Column(
        modifier = modifier,
        verticalArrangement = Arrangement.spacedBy(spacing)
    ) {
        // 범례 제목 (제공된 경우)
        title?.let {
            Text(
                text = it,
                style = MaterialTheme.typography.titleSmall,
                fontWeight = FontWeight.Bold,
                fontSize = (textSize.value + 2).sp,
                color = MaterialTheme.colorScheme.onSurface
            )
            Spacer(modifier = Modifier.height(4.dp))
        }

        // 스크롤 가능한 범례 항목들
        LazyColumn(
            modifier = Modifier.heightIn(max = maxHeight),
            verticalArrangement = Arrangement.spacedBy(spacing)
        ) {
            items(legendLabels.take(colors.size)) { label ->
                val index = legendLabels.indexOf(label)
                if (index >= 0 && index < colors.size) {
                    LegendItem(
                        color = colors[index],
                        label = label,
                        colorBoxSize = colorBoxSize,
                        textSize = textSize,
                        spacing = spacing
                    )
                }
            }
        }
    }
}
